import { useDispatch, useSelector } from "react-redux";
import { addCommentThunk, removeCommentThunk } from "../../store/modules/user/thunks";
import { useState } from "react";
import "./styles.css";
import { toast } from "react-hot-toast";

const Home = () => {
    const dispatch = useDispatch();
    const [currentComment, setCurrentCommente] = useState("");
    const [num, setNum] = useState(1);
    const user = useSelector((state) => state.user);

    const addComment = () => {
        setNum(user.comments.length + 2);
        dispatch(addCommentThunk(`${num}: ${currentComment}`));
        setCurrentCommente("");
        toast.success("Comentário adicionado com sucesso");
    };

    const removeComment = (comment) => {
        console.log(comment);
        dispatch(removeCommentThunk(comment));
        toast.error("Comentário excluido");
        setNum(user.comments.length - 1);
    };

    const attNum = () => {
        let number = 1;
        let coment = user.comments;

        for (let i = 1; i < coment.length; i++) {
            let coments = coment.split(":");
            if (coments[0] === number) {
                number + 1;
            } else {
                number;
            }
            return number;
        }
    };

    attNum();

    return (
        <div className="container">
            <h1>Anotações do {user.name}</h1>
            <div className="container_form">
                <input
                    onChange={(e) => setCurrentCommente(e.target.value)}
                    value={currentComment}
                    type={"text"}
                    placeholder="Digite seu comentário"
                ></input>
                <button onClick={addComment}>New Comment</button>
            </div>
            {user.comments.map((comment, comments) => (
                <div className="list_comments">
                    <p key={comments.index}>{comment}</p>
                    <button onClick={() => removeComment(comment)}>X</button>
                </div>
            ))}
        </div>
    );
};

export default Home;
