import { addCommentThunk } from "../../store/modules/user/thunks";
import { useState } from "react";
import "./styles.css";

const Home = () => {
    const dispatch = useDispatch();
    const [currentComment, setCurrentCommente] = useState("");
    const name = useSelector((state) => state.user);
    const comment = useSelector((state) => state.user);

    return (
        <div className="container">
            <h1>{name}</h1>
            <input
                onChange={(e) => setCurrentCommente(e.target.value)}
                value={currentComment}
                type={"text"}
                placeholder="Digite seu comentÃ¡rio"
            ></input>
            <button onClick={() => dispatch(addCommentThunk(currentComment))}>New Comment</button>

            {comment.comments.map((comment, comments) => (
                <div className="list_comments">
                    <p key={comments.index}>{comment}</p>
                    <button>X</button>
                </div>
            ))}
        </div>
    );
};

export default Home;
